import React, { useState, useEffect } from 'react';
import  api  from '../utils/api';

export default function Main({onEditProfile, onAddPlace, onEditAvatar}) {
  const [userName, setUserName] = useState('');
  const [userDescription, setUserDescription] = useState('');
  const [userAvatar, setUserAvatar] = useState('');
  const [cards, setCards] = useState([]);
  useEffect(() => {
    api.getUserInfo()
    .then((data) => {
      setUserName(data.name);
      setUserDescription(data.about);
      setUserAvatar(data.avatar);
    })
    .catch((error) => {
      console.log(error);
    });

  api.getTasks()
    .then((data) => {
     setCards(data)
     console.log(data)
    })
    .catch((error) => {
      console.log(error);
    });
  }, []);
    return (
        <main>
        <section className="profile">
          <button type="button" className="profile__avatar-button" onClick={onEditAvatar}>
            <img className="profile__avatar" src={userAvatar}  />
          </button>
          <div className="profile__info">
            <div className="profile__menu">
              <h1 className="profile__name">{userName}</h1>
              <button
                className="profile__edit-button profile__button"
                type="button"
                data-modal-btn="popup_edit-profile"
                aria-label="Кнопка открытия окна редактирования"
                onClick={onEditProfile}
              ></button>
              <p className="profile__text">{userDescription}</p>
            </div>
          </div>
          <button
            className="profile__add-button profile__button"
            type="button"
            data-modal-btn="popup_add-card"
            aria-label="Кнопка открытия окна добавления карты"
            onClick={onAddPlace}
          ></button>
        </section>
        <section className="content">
        {cards.map((card) =>
        <div className="card" id="card">
  <button
    className="card__delete"
    type="button"
    id="delete_card"
    aria-label="удаление карты"
  />
  <img
    className="card__image"
    alt="#"
    data-modal-btn="popup_increase-image"
    src={card.link}
  />
  <div className="card__info">
    <h2 className="card__name"> {card.name} </h2>
    <div className="card__like-block">
      <button className="card__like" type="button" id="cardlike" />
      <p className="card__number-likes"> {card.likes} </p>
    </div>
  </div>
</div>)}
        
        </section>
          
      </main>

    )}
