import Header from './Header';
import Main from './Main';
import Footer from './Footer';
import PopupWithForm from './PopupWithForm';
import ImagePopup from './ImagePopup'; 
import React, { useState } from 'react';


function App() {
  const [isEditProfilePopupOpen, setIsEditProfilePopupOpen] = useState(false);
  const [isAddPlacePopupOpen, setIsAddPlacePopupOpen] = useState(false);
  const [isEditAvatarPopupOpen, setIsEditAvatarPopupOpen] = useState(false); 
  const [selectedCard, setSelectedCard] = React.useState(null);

  const handleEditProfileClick = () => {
    setIsEditProfilePopupOpen(true);
  };

  const handleAddPlaceClick = () => {
    setIsAddPlacePopupOpen(true);
  };

  const handleEditAvatarClick = () => {
    setIsEditAvatarPopupOpen(true);
  };
  const closeAllPopups = () => {
    setIsEditProfilePopupOpen(false);
    setIsAddPlacePopupOpen(false);
    setIsEditAvatarPopupOpen(false);
    setSelectedCard(null);
  };
  function handleCardClick(card) {
    setSelectedCard(card);
  }
  return (
    <>
    <meta charSet="UTF-8" />
    <meta name="viewport" content="width=device-width, initial-scale=1.0" />
    <meta name="description" content="Четвертый проект" />
    <meta name="keywords" content="html,css" />
    <meta name="author" content="Илюшка" />
    <title>mesto</title>
    <section className='root'>
    <Header />
    <Main
        onEditProfile={handleEditProfileClick}
        onAddPlace={handleAddPlaceClick}
        onEditAvatar={handleEditAvatarClick}
        onCardClick={handleCardClick}
      />

    <Footer />
    <PopupWithForm isOpen={isEditProfilePopupOpen} onClose={closeAllPopups} buttonText='Сохранить' name="editProfile" title="Редактировать профиль">
    <fieldset class="popup__inputs"> 
            <input 
            id="name-input" 
            name="name" 
            class="popup__input popup__input_type_name" 
            type="text" 
            autocomplete="off" 
            placeholder="Введите имя" 
            minlength="2" 
            maxlength="40" 
            required 
          /> 
          <span class="name-input-error popup__input-error" 
            >Вы пропустили это поле</span 
          > 
          <input 
            required 
            name="job" 
            class="popup__input popup__input_type_profession" 
            type="text" 
            autocomplete="off" 
            placeholder="Расскажите о себе" 
            minlength="2" 
            maxlength="200" 
            id="profession-input" 
          /> 
            <span class="profession-input-error popup__input-error"></span> 
          </fieldset> 
</PopupWithForm>
<PopupWithForm  isOpen={isAddPlacePopupOpen} onClose={closeAllPopups} buttonText='Добавить' name="addPopupForm" title="Новое место">
<fieldset class="popup__inputs"> 
            <input 
            name="name" 
            class="popup__input popup__input_type_title" 
            type="text" 
            autocomplete="off" 
            placeholder="Название" 
            required 
            id="title-input" 
            minlength="2" 
            maxlength="30" 
          /> 
          <span class="title-input-error popup__input-error"></span> 

          <input 
            name="link" 
            class="popup__input popup__input_type_image" 
            type="url" 
            autocomplete="off" 
            placeholder="Ссылка на картинку" 
            required 
            id="url-input" 
          /> 
            <span class="url-input-error popup__input-error"></span> 
          </fieldset> 
</PopupWithForm>
<PopupWithForm  isOpen={isEditAvatarPopupOpen} onClose={closeAllPopups} buttonText='Сохранить'  name="editAvatar" title="Обновить аватар">

<fieldset class="popup__inputs"> 
            <input  
            class="popup__input popup__input_type_avatar" 
            autocomplete="off" 
            placeholder="Ссылка на картинку" 
            required 
            type="url"
            id="editAvatar"  
            name="editAvatar"
          /> 
          </fieldset>
</PopupWithForm>
<PopupWithForm name="deleteCard" title="Вы уверены?" buttonText='Вы уверены?'>
<button
            type="submit"
            class="popup__button popup__delete-button"
            aria-label="сохранить"
          >
            Да
          </button>
</PopupWithForm>

<ImagePopup 
          card={selectedCard} 
          onClose={closeAllPopups} 
        />
   </section>
   </>  
   );
}

export default App;
