import React, { useState, useEffect } from 'react';
import  api  from '../utils/api';
import Cards from './Cards';

export default function Main({onEditProfile, onAddPlace, onEditAvatar, onCardClick, handleDeleteClick}) {
  const [userName, setUserName] = useState('');
  const [userDescription, setUserDescription] = useState('');
  const [userAvatar, setUserAvatar] = useState('');
  const [cards, setCards] = useState([]);
  useEffect(() => {
    api.getUserInfo()
    .then((data) => {
      setUserName(data.name);
      setUserDescription(data.about);
      setUserAvatar(data.avatar);
    })
    .catch((error) => {
      console.log(error);
    });

  api.getTasks()
    .then((data) => {
     setCards(data)
     console.log(data)
    })
    .catch((error) => {
      console.log(error);
    });
  }, []);
    return (
        <main>
        <section className="profile">
          <button type="button" className="profile__avatar-button" onClick={onEditAvatar}>
            <img className="profile__avatar" src={userAvatar}  />
          </button>
          <div className="profile__info">
            <div className="profile__menu">
              <h1 className="profile__name">{userName}</h1>
              <button
                className="profile__edit-button profile__button"
                type="button"
                data-modal-btn="popup_edit-profile"
                aria-label="Кнопка открытия окна редактирования"
                onClick={onEditProfile}
              ></button>
              <p className="profile__text">{userDescription}</p>
            </div>
          </div>
          <button
            className="profile__add-button profile__button"
            type="button"
            data-modal-btn="popup_add-card"
            aria-label="Кнопка открытия окна добавления карты"
            onClick={onAddPlace}
          ></button>
        </section>
        <section className='cards'>
        {cards.map((card) =>
        
        <Cards 
        onDeleteClick={handleDeleteClick}
        onCardClick={onCardClick}
      link={card.link}
      name={card.name}
      likes={[...card.likes]} />
        )}
        </section>
      </main>

    )}
