import React , {useState} from "react";
import {Link} from "react-router-dom";

function Register({ onRegister }){
    const [email, setEmail] = useState("");
    const [password, setPassword] = useState("");
  
    function handleEmailChange(e) {
      setEmail(e.target.value);
    }
  
    function handlePasswordChange(e) {
      setPassword(e.target.value)
    }
    function handleSubmit(e) {
        e.preventDefault();
        onRegister(email, password);
      }
    return(
        <div className="login">
        <h1 className="login__title">Регистрация</h1>

        <form className="input__form" onSubmit={handleSubmit}>
            <input className="login__input " name="email" type="email" placeholder="Email" value={email}  onChange={handleEmailChange}></input>
            <input className="login__input " name="password" type="password" placeholder="Пароль" value={password} onChange={handlePasswordChange}></input>
            <button className="login__button" type='submit'>Зарегистрироваться</button>           
            <p className="login__link">Уже зарегистрированы?<Link to="/sign-in" className="login__link"> Войти </Link></p>
        </form>
    </div>
    )
}
export default Register
