import React, { useState, useEffect } from 'react';
import  api  from '../utils/api';

export default function Main({onEditProfile, onAddPlace, onEditAvatar}) {
  const [userName, setUserName] = useState('');
  const [userDescription, setUserDescription] = useState('');
  const [userAvatar, setUserAvatar] = useState('');
  useEffect(() => {
    const fetchData = async () => {
      const response = await api.getUserInfo();
      setUserName(response.userName);
      setUserDescription(response.userDescription);
      setUserAvatar(response.userAvatar);
    };

    fetchData();
  }, []);

    return (
        <main>
        <section className="profile">
          <button type="button" className="profile__avatar-button" onClick={onEditAvatar}>
            <img className="profile__avatar" style={{ backgroundImage: `url(${userAvatar})` }}  />
          </button>
          <div className="profile__info">
            <div className="profile__menu">
              <h1 className="profile__name">{userName}</h1>
              <button
                className="profile__edit-button profile__button"
                type="button"
                data-modal-btn="popup_edit-profile"
                aria-label="Кнопка открытия окна редактирования"
                onClick={onEditProfile}
              ></button>
              <p className="profile__text">{userDescription}</p>
            </div>
          </div>
          <button
            className="profile__add-button profile__button"
            type="button"
            data-modal-btn="popup_add-card"
            aria-label="Кнопка открытия окна добавления карты"
            onClick={onAddPlace}
          ></button>
        </section>
        <section className="content">
          <div className="cards" />
        </section>
        <template id="place-template" />
      </main>
    )}
