import React,  { useContext, useEffect, useState } from 'react';
import PopupWithForm from './PopupWithForm';
import { CurrentUserContext } from '../contexts/CurrentUserContext';

export default function EditProfilePopup({ isOpen, onClose, onUpdateUser }) {

    const currentUser = React.useContext(CurrentUserContext);
    const [name, setName] = useState(currenUser.name)
    const [about, setAbout] = useState(currenUser.about)

    useEffect(() => {
      setName(currenUser.name)
      setAbout(currenUser.about)
    }, [currenUser])
  
    function handleChangeName(e) {
      setName(e.target.value)
    }
  
    function handleChangeDiscr(e) {
      setAbout(e.target.value)
    }
  
    function handleSubmit(e) {
      e.preventDefault()
      onUpdateUser({
        name,
        about
      })
    }

    return ( 
    <PopupWithForm
        isOpen={isOpen}
        onClose={onClose}
        buttonText="Сохранить"
        onSubmit={handleSubmit}
        name="editProfile"
        title="Редактировать профиль"
        children={
        <fieldset className="popup__inputs">
        <input
          id="name-input"
          name="name"
          className="popup__input popup__input_type_name"
          type="text"
          autoComplete="off"
          placeholder="Введите имя"
          minLength="2"
          maxLength="40"
          required
          onChange={handleNameChange}
          value={name || ''}
        />
        <span className="name-input-error popup__input-error">
          Вы пропустили это поле
        </span>
        <input
          required
          name="job"
          className="popup__input popup__input_type_profession"
          type="text"
          autoComplete="off"
          placeholder="Расскажите о себе"
          minLength="2"
          maxLength="200"
          id="profession-input"
          onChange={handleDescriptionChange}
          value={description  || ''}
        />
        <span className="profession-input-error popup__input-error"></span>
      </fieldset>}
      />
)}
