export default function Main() {
    const handleEditAvatarClick = () => {
        const popup = document.querySelector('.popup_profile_avatar');
        popup.classList.add('popup_opened');
      };
      
      const handleEditProfileClick = () => {
        const popup = document.querySelector('.popup_type_profile');
        popup.classList.add('popup_opened');
      };
      
      const handleAddPlaceClick = () => {
        const popup = document.querySelector('.popup_add_card');
        popup.classList.add('popup_opened');
      };

    return (
        <main>
        <section className="profile">
          <button type="button" className="profile__avatar-button" onClick={handleEditAvatarClick}>
            <img className="profile__avatar" />
          </button>
          <div className="profile__info">
            <div className="profile__menu">
              <h1 className="profile__name">Жак-Ив Кусто</h1>
              <button
                className="profile__edit-button profile__button"
                type="button"
                data-modal-btn="popup_edit-profile"
                aria-label="Кнопка открытия окна редактирования"
                onClick={handleEditProfileClick}
              ></button>
              <p className="profile__text">Исследователь океана</p>
            </div>
          </div>
          <button
            className="profile__add-button profile__button"
            type="button"
            data-modal-btn="popup_add-card"
            aria-label="Кнопка открытия окна добавления карты"
            onClick={handleAddPlaceClick}
          ></button>
        </section>
        <section className="content">
          <div className="cards" />
        </section>
        <template id="place-template" />
      </main>
    )}
