export default function PopupWithForm({
   name,
  title,
  isOpen,
  onClose,
  onSubmit,
  children,
  buttonText,}) {
  function handeSubmit(e) {
    e.preventDefault()
    console.log(e)
  }
  return (
    <>
      <div
        className={`popup popup_type_${name} ${
          isOpen ? "popup_opened" : ""
        }`}
      >
        <div className="popup__container">
          <button
            className="popup__exit"
            type="button"
            id="close_edit"
            aria-label="Кнопка закрытия"
            onClick={onClose}
          ></button>
          <h2 className="popup__title">{title}</h2>
          <form className="form popup__form" name={props.name} onSubmit={onSubmit}>
            {props.children}
          </form>

          <button
            className="popup__button"
            type={"submit"}
            aria-label="Кнопка сохранения новой информации о профиле"
          >
            {props.buttonText}
          </button>
        </div>
      </div>
    </>
  );
}
