import React, { useState, useEffect } from "react";
import { CurrentUserContext } from "../contexts/CurrentUserContext";
import Header from "./Header";
import Main from "./Main";
import Footer from "./Footer";
import PopupWithForm from "./PopupWithForm";
import ImagePopup from "./ImagePopup";
import api from "../utils/api.js";

function App() {
  const [isEditProfilePopupOpen, setIsEditProfilePopupOpen] = useState(false);
  const [isAddPlacePopupOpen, setIsAddPlacePopupOpen] = useState(false);
  const [isEditAvatarPopupOpen, setIsEditAvatarPopupOpen] = useState(false);
  const [isDeletePopupOpen, setIsDeletePopupOpen] = useState(false);
  const [selectedCard, setSelectedCard] = React.useState(null);
  const [currentUser, setCurrentUser] = useState({});
  const [cards, setCards] = useState([]);

  useEffect(() => {
    api.getUserInfo().then(setCurrentUser).catch(console.log);
    // api.getCards().then(setCards).catch(console.log);
  }, []);

  function handleCardLike(card) {
    const isLiked = card.likes.some(i => i._id === currentUser._id);

    api.changeLikeCardStatus(card._id, !isLiked)
      .then((newCard) => {
        setCards((state) => state.map((c) => c._id === card._id ? newCard : c));
      })
      .catch((err) => {
        console.log('Ошибка при обновлении статуса лайка:', err);
      });
  }

  const handleEditProfileClick = () => {
    setIsEditProfilePopupOpen(true);
  };

  const handleDeleteClick = () => {
    setIsDeletePopupOpen(true);
  };

  const handleAddPlaceClick = () => {
    setIsAddPlacePopupOpen(true);
  };

  const handleEditAvatarClick = () => {
    setIsEditAvatarPopupOpen(true);
  };
  const closeAllPopups = () => {
    setIsDeletePopupOpen(false);
    setIsEditProfilePopupOpen(false);
    setIsAddPlacePopupOpen(false);
    setIsEditAvatarPopupOpen(false);
    setSelectedCard(null);
  };
  function handleCardClick(card) {
    setSelectedCard(card);
  }
  return (
    <CurrentUserContext.Provider value={currentUser}>
      <section className="root">
        <Header />
        <Main
          onEditProfile={handleEditProfileClick}
          onAddPlace={handleAddPlaceClick}
          onEditAvatar={handleEditAvatarClick}
          onCardClick={handleCardClick}
          onDeleteClick={handleDeleteClick}
          onCardLike={handleCardLike}
        />

        <Footer />
        <PopupWithForm
          isOpen={isEditProfilePopupOpen}
          onClose={closeAllPopups}
          buttonText="Сохранить"
          name="editProfile"
          title="Редактировать профиль"
        >
          <fieldset className="popup__inputs">
            <input
              id="name-input"
              name="name"
              className="popup__input popup__input_type_name"
              type="text"
              autoComplete="off"
              placeholder="Введите имя"
              minLength="2"
              maxLength="40"
              required
            />
            <span className="name-input-error popup__input-error">
              Вы пропустили это поле
            </span>
            <input
              required
              name="job"
              className="popup__input popup__input_type_profession"
              type="text"
              autoComplete="off"
              placeholder="Расскажите о себе"
              minLength="2"
              maxLength="200"
              id="profession-input"
            />
            <span className="profession-input-error popup__input-error"></span>
          </fieldset>
        </PopupWithForm>
        <PopupWithForm
          isOpen={isAddPlacePopupOpen}
          onClose={closeAllPopups}
          buttonText="Добавить"
          name="addPopupForm"
          title="Новое место"
        >
          <fieldset className="popup__inputs">
            <input
              name="name"
              className="popup__input popup__input_type_title"
              type="text"
              autoComplete="off"
              placeholder="Название"
              required
              id="title-input"
              minLength="2"
              maxLength="30"
            />
            <span className="title-input-error popup__input-error"></span>

            <input
              name="link"
              className="popup__input popup__input_type_image"
              type="url"
              autoComplete="off"
              placeholder="Ссылка на картинку"
              required
              id="url-input"
            />
            <span className="url-input-error popup__input-error"></span>
          </fieldset>
        </PopupWithForm>
        <PopupWithForm
          isOpen={isEditAvatarPopupOpen}
          onClose={closeAllPopups}
          buttonText="Сохранить"
          name="editAvatar"
          title="Обновить аватар"
        >
          <fieldset className="popup__inputs">
            <input
              className="popup__input popup__input_type_avatar"
              autoComplete="off"
              placeholder="Ссылка на картинку"
              required
              type="url"
              id="editAvatar"
              name="editAvatar"
            />
          </fieldset>
        </PopupWithForm>
        <PopupWithForm
          isOpen={isDeletePopupOpen}
          onClose={closeAllPopups}
          name="deleteCard"
          title="Вы уверены?"
          buttonText="Да"

        ></PopupWithForm>

        <ImagePopup card={selectedCard} onClose={closeAllPopups} />
      </section>
      </CurrentUserContext.Provider>
  );
}

export default App;
